import React from "react";
import './add-panel.css';
import '../data-table'

const AddPanel= ({onDeleted})=>
{
    handleClick = () => {
        const { onDelete, id } = this.props;
        onDelete(id);
      };

    return (
        <div className='container text-center'>
            <input type="text" className='add-panel-name' placeholder="Name" />
            <input type="text" className='add-panel-birthday' placeholder="Birthday" />
            <input type="text" className='add-panel-points' placeholder="Points" />
            <button type="button" className="button-add btn-info">
            Add
            </button>
            <button type="button" className="button-add btn-info">
            Red
            </button>
            <button type="button" className="button-add btn-danger" onClick={this.handleClick}>
            Del
            </button>
        </div>
    )
};

export default AddPanel;
