import React, { Component } from "react";
import './add-panel.css';
import '../data-table'

export default class AddPanel  extends Component {

    constructor(props) {
        super(props);
        this.state = {
            name: '',
            birthday: '',
            points: 0,
        };
    }

    handleNameChange = (event) => {
        this.setState({ name: event.target.value });
    }

    handleBirthdayChange = (event) => {
        this.setState({ birthday: event.target.value });
    }

    handlePointsChange = (event) => {
        this.setState({ points: event.target.value });
    }
    handleAddNewItem = () => {
        this.props.onAddNewItem(this.props.newItem);
    }

    render(){
        const { onDelete, onAdd, id, item, newItem} = this.props;
        return (

                <div className='container text-center'>
                    <input 
                        type="text" 
                        className='add-panel-name' 
                        placeholder="Name" 
                        value={this.state.name} 
                        onChange={this.handleNameChange} />
                    <input 
                        type="text" 
                        className='add-panel-birthday' 
                        placeholder="Birthday" 
                        value={this.state.birthday} 
                        onChange={this.handleBirthdayChange} />
                    <input 
                        type="text" 
                        className='add-panel-points' 
                        placeholder="Points" 
                        value={this.state.points} 
                        onChange={this.handlePointsChange}/>
                    <button type="button" className="button-add btn-info" onClick={this.handleAddNewItem}>
                    Add
                    </button>
                    <button type="button" className="button-add btn-info">
                    Red
                    </button>
                    <button type="button" className="button-add btn-danger" onClick={onDelete}>
                    Del
                    </button>
                </div>
            )
    }
}
