import React from 'react';
import PointsOfTable from './points-of-table';

const DataTable = ({datas})=> {
    // Превратим массив объектов в массив JSX-компонентов
    // строка {...item} равносильна строке label ={item.label} important ={item.important}. То есть мы разделяем item на свойства и передаем их как значение компоента PointsOfTable
    const elements = datas.map ((item) => {
        return (
            <li>
            <PointsOfTable {...item}  />
            </li>)
    })
    return (
        <ul>
        {elements}
        </ul>
    );
};
export default DataTable;
