import React, { Component } from 'react';

import './data-from-server.css';


class DataFromServer extends Component {
    constructor (props){
      super(props);
      this.state={
        item:[],          //массив данных
        isLoaded:false    //статус
      }
    }

    componentDidMount(){
        fetch('https://my.api.mockaroo.com/shop_customers.json?key=032062d0')
        .then(res=>res.json())
        .then(json=>{
          this.setState({
            isLoaded:true,
            items:json
          })
        });
      }

    render() { 
        var {isLoaded, items}=this.state;
    if (!isLoaded){
      return (
      <div> 'Loading..'</div>)}
      else{
        return (
            <div>
                <ul>
                    {items.map(item => (
                        <li></li>
                    ))}
                </ul>
            </div>
        );
    }
    }
}

export default DataFromServer;
