import React, { Component } from "react";
import './add-panel.css';
import '../data-table'

export default class AddPanel  extends Component {

    constructor(props) {
        super(props);
        this.state = {
            name: '',
            birthday: '',
            points: ''
          };
    }


    render(){
        const { onDelete, onAdd, id, item, newItem} = this.props;
        return (

                <div className='container text-center'>
                    <input type="text" className='add-panel-name' placeholder="Name" />
                    <input type="text" className='add-panel-birthday' placeholder="Birthday" />
                    <input type="text" className='add-panel-points' placeholder="Points" />
                    <button type="button" className="button-add btn-info" onClick={onAdd}>
                    Add
                    </button>
                    <button type="button" className="button-add btn-info">
                    Red
                    </button>
                    <button type="button" className="button-add btn-danger" onClick={onDelete}>
                    Del
                    </button>
                </div>
            )
    }
}
