import React, { Component } from 'react';

import './data-from-server.css';


class DataFromServer extends Component {
    constructor (props){
      super(props);
      this.state={
        item:[],          //массив данных
        isLoaded:false    //статус
      }
    }

    // changeCss(event){
    //     event.currentTarget.style.background = 'tomato';
    // }

    componentDidMount(){
        fetch('https://my.api.mockaroo.com/shop_customers.json?key=032062d0')
        .then(res=>res.json())
        .then(json=>{
          this.setState({
            isLoaded:true,
            items:json
          })
        });
      }


    render() { 
    var {isLoaded, items}=this.state;

    console.log(items);
    if (!isLoaded){
      return (
      <div> 'Loading..'</div>)}
      else{
        return (
            <div className='row row-cols-1 row-cols-md-2 row-cols-lg-3' >
                <div className='col d-inline-block item-top text-center'>
                    <ul>
                        {items.map(item => (

                            <li onClick={() =>{console.log("Click on li")}}>{item.first_name} {item.last_name}</li>
                        ))}
                    </ul>   
                </div>
                <div className='col d-inline-block item-top text-center'>
                    <ul>
                        {items.map(item => (

                            <li>{item.birthday}</li>
                        ))}
                    </ul>   
                </div>
                <div className='col d-inline-block item-top text-center'>
                    <ul>
                        {items.map(item => (

                            <li>{item.accumulated_points}</li>
                        ))}
                    </ul>   
                </div>
            </div>
        );
    }  
    }
}

export default DataFromServer;
