import React, { Component } from 'react';

import './data-from-server.css';


class DataFromServer extends Component {
    constructor (props){
      super(props);
      this.state={
        item:[],          //массив данных
        isLoaded:false,    //статус
        mark: false
      }
    }

    markList = () => {
       this.mark=true; 

       console.log("in-mark-list", this.mark);
       return this.mark

        // event.currentTarget.style.background = 'pink';
    }

    componentDidMount(){
        fetch('https://my.api.mockaroo.com/shop_customers.json?key=032062d0')
        .then(res=>res.json())
        .then(json=>{
          this.setState({
            isLoaded:true,
            items:json
          })
        });
      }


    render() { 
    let {isLoaded, items, mark} = this.props;


    let lineStyle = {
        backgroundColor: mark ? 'red' : 'none' } ;


    console.log("mark=", mark);
    if (!isLoaded){
      return (
      <div> 'Loading..'</div>)}
      else{
        return (
            <div className='container  ' >
                <ul className='without-padding' >
                        {items.map(item => (
                            <div style={lineStyle} className='one-list' onClick={this.markList}>
                                <div className='list-id'>
                                    <li> {item.id}</li>
                                </div>
                                <div className='list-name'>
                                    <li> {item.first_name} {item.last_name}</li> 
                                </div>
                                <div className='list-birthday'>
                                    <li> {item.birthday}</li>
                                </div>
                                <div className='list-points'>
                                    <li> {item.accumulated_points}</li>
                                </div>

                            </div>

                        ))}
                    </ul>   
                <div className='col d-inline-block item-top text-center'>

                </div>
                {/* <li className='one-list' onClick={this.changeCss}>{item.first_name} {item.last_name}      {item.birthday}</li> */}
                {/* <div className='col d-inline-block item-top text-center'>
                    <ul>
                        {items.map(item => (

                            <li>{item.birthday}</li>
                        ))}
                    </ul>   
                </div>
                <div className='col d-inline-block item-top text-center'>
                    <ul>
                        {items.map(item => (

                            <li>{item.accumulated_points}</li>
                        ))}
                    </ul>   
                </div> */}
            </div>
        );
    }  
    }
}

export default DataFromServer;
