import React, { Component } from 'react';

import './data-table.css';
import '../add-panel'
import AddPanel from '../add-panel';



export default class DataTable extends Component {
    constructor (props){
        super(props);
        this.state={
            item:[],          //массив данных
            newItem: {
                id: 0,
                first_name: "",
                last_name:"",
                birthday:"",
                accumulated_points:0
            },
            isLoaded:false,    //статус
            mark:false,
            selectedItem: null,
        };
    }

    markList = (event, itemId) => {
        this.setState(({mark}) => {
            return { mark: !mark }
        })
        this.setState(({selectedItem})=>{
            return {selectedItem: itemId}
        });
    }

    deleteItem = (id) => {
        const updatedItem = this.state.item.filter((item) => item.id !== this.state.selectedItem);
        this.setState(({item})=>{
            return {item: updatedItem}
        });
    };

    addItem = (newItem) =>{
        const currentItem = this.state.item; //массив данных до добавления элемента
        const newItemWithId = {
            ...newItem,
            id: Math.max(...currentItem.map(el => el.id)) + 1,
        };                                  //массив данных после добавления нового элемента
        console.log("newItemWithId",newItemWithId);
        this.setState(({item}) => ({
            item: [...item, newItemWithId],
            selectedItem: newItem.id
        }));
        console.log("this.state.item = ", this.state.item);
    };


    componentDidMount(){
        fetch('https://my.api.mockaroo.com/shop_customers.json?key=032062d0')
        .then(res=>res.json())
        .then(json=>{
            this.setState({
                isLoaded:true,
                item:json,
            })
        });

    }



    render() { 
        let {isLoaded, item, newItem, selectedItem} = this.state;

        if (!isLoaded){
            return (
                <div className='container row row-cols-1'> 
                    <span className='text-center'>'Loading data..'</span>
                </div>
            )
        }

        else{
            return (
                <div className='container  ' >
                    <ul className='without-padding' >
                        {item.map(item => (
                            <div  
                            className={` ${
                                ((!this.state.mark)&&(item.id === selectedItem)) ? 'one-list-marked' : 'one-list'}`} 
                            onClick={(e) => this.markList(e, item.id)}>
                                <div className='list-id'>
                                    <li> {item.id}</li>
                                </div>
                                <div className='list-name' >
                                    <li> {item.first_name} {item.last_name}</li> 
                                </div>
                                <div className='list-birthday'>
                                    <li> {item.birthday}</li>
                                </div>
                                <div className='list-points'>
                                    <li> {item.accumulated_points}</li>
                                </div>

                            </div>
                        ))}
                        </ul>
                        <AddPanel 
                            onDelete={() =>{ this.deleteItem(selectedItem) }}
                            onAddNewItem={() => this.addItem(newItem)}
                            id={selectedItem}
                            item={item.find(item => item.id === selectedItem)}
                            newItem={this.state.newItem}
                            onNewItemChange={(newItem) => this.setState({ newItem })}
                        />
                </div>
            );
        }  
    }
}
