import React, { Component } from "react";
import './add-panel.css';
import '../data-table'

export default class AddPanel  extends Component {

    constructor(props) {
        super(props);
        this.state = {
            item:{
                id: 0,
                first_name: "",
                last_name:"",
                birthday:"",
                accumulated_points:0},
            btnRedPressed:false,
        };
    }

    handleNameChange = (event) => {
        this.setState({ first_name: event.target.value });
        console.log(this.state);
    }

    handleBirthdayChange = (event) => {
        this.setState({ birthday: event.target.value });
        console.log(this.state);

    }

    handlePointsChange = (event) => {
        this.setState({ accumulated_points: event.target.value });
        console.log(this.state);

    }
    handleAddNewItem = (event) => {
        console.log("input_data=",this.state);
        this.props.onAddNewItem(this.props.newItem);
    }
    redItem = (item, mark) => {
        this.setState({btnRedPressed:!this.state.btnRedPressed}) //поменять статус кнопки
        console.log("btn-status",this.state.btnRedPressed);
        if (mark) 
            this.setState(({item})=>({
                first_name: this.props.item.first_name,
                birthday: this.props.item.birthday,
                accumulated_points: this.props.item.accumulated_points,
            }));

    }
    render(){
        const { onDelete, item, mark } = this.props;

        console.log("item=",item);
        console.log("this.state.item= ", this.state.item);

        return (

                <div className='container text-center'>
                    <input 
                        type="text" 
                        className='add-panel-name' 
                        placeholder="Name" 
                        value={this.state.first_name} 
                        onChange={this.handleNameChange} />
                    <input 
                        type="text" 
                        className='add-panel-birthday' 
                        placeholder="Birthday" 
                        value={this.state.birthday} 
                        onChange={this.handleBirthdayChange} />
                    <input 
                        type="text" 
                        className='add-panel-points' 
                        placeholder="Points" 
                        value={this.state.accumulated_points} 
                        onChange={this.handlePointsChange}/>
                    <button 
                        type="button" 
                        className="button-add btn-info" 
                        onClick={() =>this.props.onAddNewItem(this.state)}>
                    Add
                    </button>
                    <button 
                        type="button" 
                        className={this.state.btnRedPressed?'button-add btn-outline-info':'button-add btn-info'}
                        onClick={()=>this.redItem(item,mark)}>

                    Red
                    </button>
                    <button 
                        type="button" 
                        className="button-add btn-danger" 
                        onClick={onDelete}>
                    Del
                    </button>
                </div>
            )
    }
}
