import React, { Component } from 'react';

import './data-table.css';
import '../add-panel'
import AddPanel from '../add-panel';



export default class DataTable extends Component {
    constructor (props){
        super(props);
        this.state={
            item:[],          //массив данных
            isLoaded:false,    //статус
            mark:false,
            selectedItem: null,
        };
    }

    markList = (event, itemId) => {
        this.setState(({mark}) => {
            return { mark: !mark }
        })
        // event.currentTarget.style.background = this.state.mark ? 'pink' : 'none';
        this.setState(({selectedItem})=>{
            return {selectedItem: itemId}
        });
        console.log("itemId= ",itemId);
        console.log("mark= ",this.state.mark);
        console.log("this.state.selectedItem= ",this.state.selectedItem);
    }

    deleteItem = (id) => {
        const updatedItem = this.state.item.filter((item) => item.id !== id);
        this.setState({ item: updatedItem });
    };

    componentDidMount(){
        fetch('https://my.api.mockaroo.com/shop_customers.json?key=032062d0')
        .then(res=>res.json())
        .then(json=>{
            this.setState({
                isLoaded:true,
                items:json,
            })
        });
    }



    render() { 
        let {isLoaded, items, mark} = this.state;
        let oneListClass = mark ? 'one-list-marked' : 'one-list';
        if (!isLoaded){
            return (
                <div className='container row row-cols-1'> 
                    <span className='text-center'>'Loading data..'</span>
                </div>
            )
        }

        else{
            return (
                <div className='container  ' >
                    <ul className='without-padding' >
                        {items.map(item => (
                            <div  
                            className={` ${
                                item.id === this.state.selectedItemId ? 'one-list-marked' : 'one-list'}`} 
                            onClick={(e) => this.markList(e, item.id)}>
                                <div className='list-id'>
                                    <li> {item.id}</li>
                                </div>
                                <div className='list-name' >
                                    <li> {item.first_name} {item.last_name}</li> 
                                </div>
                                <div className='list-birthday'>
                                    <li> {item.birthday}</li>
                                </div>
                                <div className='list-points'>
                                    <li> {item.accumulated_points}</li>
                                </div>

                            </div>

                        ))}
                        </ul>

                </div>
            );
        }  
    }
}
