import React from "react";
import './points-of-table.css';

const PointsOfTable =({label, important=false}) => {
    const splanStyle = {
        color: important ? 'tomato' : 'black'
    }
    return <span style= {splanStyle} > {label}</span>;
};

export default PointsOfTable;
