async function componentDidMountTest () {
    const state = {
        item:[],          //массив данных
        isLoaded:false,    //статус
        mark:false,
    }
    try {
        const res = await fetch('https://my.api.mockaroo.com/shop_customers.json?key=032062d0');
        const json = await res.json();
        state.item = json;
        state.isLoaded = true;
        return state;
    } catch (error) {
        console.error('Ошибка получения данных:', error);
    }
    return state; // возвращаем обновленное значение state
}

export default componentDidMountTest
