import React, { Component } from 'react';
import  ReactDOM  from 'react-dom/client';

import AddPanel from '../add-panel'  
import AppHeader from '../app-header';
import DataTable from '../data-table';
import HeadData from '../head-table';
import AddData from '../add-data';

// import componentDidMountTest from '../data-from-server/data-from-server'

function componentDidMountTest () {
    const state = {
        item:[],          //массив данных
        isLoaded:false,    //статус
        mark:false,
    }
    fetch('https://my.api.mockaroo.com/shop_customers.json?key=032062d0')
        .then(res=>res.json())
        .then(json=>{
            state.isLoaded = true;
            state.item=json;
            // console.log("state= ",state);
            return state;
        });
}

const dataTest = componentDidMountTest();
console.log("dataTest= ",dataTest);

export default class App extends Component {

    state = {
        items: [
            { num: 11,
            name: "NNNNN FFFFFFFF",
            birthday: "12.11.1995",
            points: 300 }   
        ]
    }


    render () {
        return (
            <div className="rowcol-lg-4 col-md-6 mx-auto">
            <AppHeader/>
            <AddPanel onDeleted={()=>{console.log("DeleteClick")}}/>
            <HeadData/>
            <DataTable/>
            <AddData
                num={this.state.num} 
                name={this.state.name} 
                birthday={this.state.birthday} 
                points={this.state.points}/>
            </div>);
    }

}
