import React, { Component } from 'react';

import './data-from-server.css';


export default class DataFromServer extends Component {
    constructor (props){
      super(props);
      this.state={
        item:[],          //массив данных
        isLoaded:false,    //статус
        mark:false,
        isDone:false
      };
    }

    markList = (event) => {
        this.state.mark=!this.state.mark;
        this.state.isDone=!this.state.isDone;
        console.log("isDone=", this.state.isDone);
        event.currentTarget.style.background = this.state.mark ? 'pink' : 'none';
    }

    componentDidMount(){
        fetch('https://my.api.mockaroo.com/shop_customers.json?key=032062d0')
        .then(res=>res.json())
        .then(json=>{
          this.setState({
            isLoaded:true,
            items:json,
          })
        });
      }


    render() { 
    let {isLoaded, items, isDone} = this.state;
    let oneClassName = 'one-list';

    if (isDone) {
        oneClassName+=' one-list-marked';

    }
    console.log(oneClassName);
    if (!isLoaded){
    return (
    <div className='container row row-cols-1'> 
        <span className='text-center'>'Loading data..'</span>
    </div>)}
    else{
    return (
        <div className='container  ' >
            <ul className='without-padding' >
                {items.map(item => (
                    <div  className={oneClassName} onClick={this.markList}>
                        <div className='list-id'>
                            <li> {item.id}</li>
                        </div>
                        <div className='list-name' >
                            <li> {item.first_name} {item.last_name}</li> 
                        </div>
                        <div className='list-birthday'>
                            <li> {item.birthday}</li>
                        </div>
                        <div className='list-points'>
                            <li> {item.accumulated_points}</li>
                        </div>

                    </div>

                ))}
                </ul>   
        </div>
    );
}  
    }
}
