import React, { Component } from 'react';

import './data-table.css';
import componentDidMountTest from '../data-from-server/data-from-server'



export default class DataTable extends Component {
    constructor (props){
        super(props);
        this.state={
            item:[],          //массив данных
            isLoaded:false,    //статус
            mark:false,
        };
    }

    markList = (event) => {
        this.setState(({mark}) => {
            return { mark: !mark }
        })
        // this.state.mark=!this.state.mark;
        event.currentTarget.style.background = this.state.mark ? 'pink' : 'none';
    }

    // componentDidMount(){
    //     fetch('https://my.api.mockaroo.com/shop_customers.json?key=032062d0')
    //     .then(res=>res.json())
    //     .then(json=>{
    //         this.setState({
    //             isLoaded:true,
    //             items:json,
    //         })
    //     });
    // }



    render() { 
        let dataForTable = componentDidMountTest();
        console.log("dataForTable= ", dataForTable);
        let {isLoaded, items} = this.state;

        if (!isLoaded){
            return (
                <div className='container row row-cols-1'> 
                    <span className='text-center'>'Loading data..'</span>
                </div>
            )
        }

        else{
            return (
                <div className='container  ' >
                    <ul className='without-padding' >
                        {items.map(item => (
                            <div  className='one-list' onClick={this.markList}>
                                <div className='list-id'>
                                    <li> {item.id}</li>
                                </div>
                                <div className='list-name' >
                                    <li> {item.first_name} {item.last_name}</li> 
                                </div>
                                <div className='list-birthday'>
                                    <li> {item.birthday}</li>
                                </div>
                                <div className='list-points'>
                                    <li> {item.accumulated_points}</li>
                                </div>

                            </div>

                        ))}
                        </ul>   
                </div>
            );
        }  
    }
}
