import React, { Component } from 'react';

import './data-from-server.css';


class DataFromServer extends Component {
    constructor (props){
      super(props);
      this.state={
        item:[],          //массив данных
        isLoaded:false    //статус
      }
    }

    changeCss(event){
        event.currentTarget.style.color = 'tomato';
    }

    componentDidMount(){
        fetch('https://my.api.mockaroo.com/shop_customers.json?key=032062d0')
        .then(res=>res.json())
        .then(json=>{
          this.setState({
            isLoaded:true,
            items:json
          })
        });
      }


    render() { 
    var {isLoaded, items}=this.state;

    console.log(items);
    if (!isLoaded){
      return (
      <div> 'Loading..'</div>)}
      else{
        return (
            <div className='container  row ' >
                <div className='col d-inline-block item-top text-center'>
                    <ul>
                        {items.map(item => (

                            <li onClick={this.changeCss}>{item.first_name} {item.last_name}      {item.birthday}</li>
                        ))}
                    </ul>   
                </div>
                {/* <div className='col d-inline-block item-top text-center'>
