function componentDidMountTest () {
    const state = {
        item:[],          //массив данных
        isLoaded:false,    //статус
        mark:false,
    }
    fetch('https://my.api.mockaroo.com/shop_customers.json?key=032062d0')
        .then(res=>res.json())
        .then(json=>{
            state.isLoaded = true;
            state.item=json;
            // console.log("state= ",state);
            return state;
        });
    return state; // возвращаем обновленное значение state
}

export default componentDidMountTest
