import React, { Component } from 'react';

import './data-table.css';
import '../add-panel'
import AddPanel from '../add-panel';



export default class DataTable extends Component {
    constructor (props){
        super(props);
        this.state={
            item:[],          //массив данных
            isLoaded:false,    //статус
            mark:false,
            selectedItem: null,
            testItems : [
                {id:1,
                first_name:"Denver",
                last_name:"Broadway",birthday:"13.02.1970",accumulated_points:884},{id:2,first_name:"Tremain",last_name:"Allenson",birthday:"26.07.1985",accumulated_points:104},{id:3,first_name:"Blaire",last_name:"Hoofe",birthday:"21.08.1979",accumulated_points:916},{id:4,first_name:"Alyson",last_name:"Rawdales",birthday:"03.02.1968",accumulated_points:987},{id:5,first_name:"Concettina",last_name:"Frissell",birthday:"06.09.1999",accumulated_points:206}]
        };
    }


    selectedItem = (event, itemId) => {
        this.setState(({mark}) => {
            return { mark: !mark }
        })
        this.setState(({selectedItem})=>{
            return {selectedItem: itemId}
        });
    }

    deleteItem = (id) => {
        const updatedItem = this.state.item.filter((item) => item.id !== this.state.selectedItem);
        this.setState(({item})=>{
            return {item: updatedItem}
        });
    };

    addItem = (newItem) =>{
        const currentItem = this.state.item; //массив данных до добавления элемента
        const newItemWithId = {
            ...newItem,
            id: Math.max(...currentItem.map(el => el.id)) + 1,
        };                                  
        this.setState(({item}) => ({
            item: [...item, newItemWithId],
            selectedItem: newItemWithId.id
        }));    
    };

    redItem = () => {

    }


    componentDidMount () {
            fetch('https://my.api.mockaroo.com/shop_customers.json?key=032062d0')
                .then(res=>res.json())
                .then(json=>{
                    this.setState({
                        isLoaded:true,
                        item:json,
                    })
                });
    }



    render() { 
        let {item, isLoaded, selectedItem} = this.state;
        if (!isLoaded){
            return (
                <div className='container row row-cols-1'> 
                    <span className='text-center'>'Loading data..'</span>
                </div>
            )
        }
        if (item.error) {
            this.setState(({item})=>{
                return {item: this.state.testItems}
            });
        }
        else{
            return (
                <div className='container  ' >
                    <ul className='without-padding' >
                        {item.map(item => (
                            <div  
                            className={` ${
                                ((!this.state.mark)&&(item.id === selectedItem)) ? 'one-list-marked' : 'one-list'}`} 
                            onClick={(e) => this.selectedItem(e, item.id)}>
                                <div className='list-id'>
                                    <li> {item.id}</li>
                                </div>
                                <div className='list-name' >
                                    <li> {item.first_name} {item.last_name}</li> 
                                </div>
                                <div className='list-birthday'>
                                    <li> {item.birthday}</li>
                                </div>
                                <div className='list-points'>
                                    <li> {item.accumulated_points}</li>
                                </div>

                            </div>
                        ))}
                        </ul>
                        <AddPanel 
                            onDelete={() =>{ this.deleteItem(selectedItem) }}
                            onAddNewItem={this.addItem}
                            id={selectedItem}
                            item={item.find(item => item.id === selectedItem)} //выделенный элемент таблицы
                        />
                </div>
            );
        }  
    }
}
